ect";s:0:"";s:20:"error_contact_select";s:0:"";s:13:"error_subject";s:0:"";s:10:"error_name";s:0:"";s:11:"error_field";s:0:"";s:12:"error_select";s:0:"";s:11:"error_email";s:0:"";s:17:"error_email_check";s:0:"";s:12:"error_email2";s:0:"";s:9:"error_url";s:0:"";s:10:"error_date";s:0:"";s:10:"error_time";s:0:"";s:12:"error_maxlen";s:0:"";s:19:"error_captcha_blank";s:0:"";s:19:"error_captcha_wrong";s:0:"";s:13:"error_spambot";s:0:"";s:11:"error_input";s:0:"";s:18:"captcha_perm_level";s:4:"read";s:19:"akismet_send_anyway";s:4:"true";s:14:"domain_protect";s:4:"true";s:20:"domain_protect_names";s:0:"";s:22:"auto_respond_from_name";s:5:"hawkd";s:23:"auto_respond_from_email";s:15:"hej@jenserik.se";s:21:"auto_respond_reply_to";s:15:"hej@jenserik.se";s:20:"auto_respond_subject";s:0:"";s:20:"auto_respond_message";s:0:"";s:15:"redirect_enable";s:4:"true";s:16:"redirect_seconds";i:3;s:12:"redirect_url";s:22:"http://localhost/hawkd";s:15:"redirect_ignore";s:0:"";s:15:"redirect_rename";s:0:"";s:12:"redirect_add";s:0:"";s:17:"submit_attributes";s:0:"";s:15:"form_attributes";s:0:"";s:13:"anchor_enable";s:4:"true";s:22:"enable_submit_oneclick";s:4:"true";s:15:"after_form_note";s:0:"";s:17:"success_page_html";s:0:"";s:17:"php_mailer_enable";s:9:"wordpress";s:18:"sender_info_enable";s:4:"true";s:11:"silent_send";s:3:"off";s:10:"silent_url";s:0:"";s:13:"silent_ignore";s:0:"";s:13:"silent_rename";s:0:"";s:10:"silent_add";s:0:"";s:24:"silent_conditional_field";s:0:"";s:24:"silent_conditional_value";s:0:"";s:13:"export_ignore";s:0:"";s:13:"export_rename";s:0:"";s:10:"export_add";s:0:"";s:14:"vcita_approved";s:5:"false";s:9:"vcita_uid";s:0:"";s:11:"vcita_email";s:0:"";s:15:"vcita_email_new";s:15:"hej@jenserik.se";s:16:"vcita_first_name";s:0:"";s:15:"vcita_last_name";s:0:"";s:29:"vcita_scheduling_button_label";s:23:"Schedule an Appointment";s:26:"vcita_scheduling_link_text";s:68:"Click above to schedule an appointment using vCita Online Scheduling";s:19:"email_from_enforced";s:5:"false";s:21:"preserve_space_enable";s:5:"false";s:12:"double_email";s:5:"false";s:16:"name_case_enable";s:5:"false";s:15:"email_check_dns";s:5:"false";s:16:"email_check_easy";s:5:"false";s:10:"email_html";s:5:"false";s:15:"akismet_disable";s:5:"false";s:14:"captcha_enable";s:5:"false";s:13:"captcha_small";s:5:"false";s:16:"email_hide_empty";s:5:"false";s:22:"email_keep_attachments";s:5:"false";s:17:"print_form_enable";s:5:"false";s:12:"captcha_perm";s:5:"false";s:15:"honeypot_enable";s:5:"false";s:14:"redirect_query";s:5:"false";s:18:"redirect_email_off";s:5:"false";s:16:"silent_email_off";s:5:"false";s:16:"export_email_off";s:5:"false";s:19:"ex_fields_after_msg";s:5:"false";s:18:"email_inline_label";s:5:"false";s:19:"textarea_html_allow";s:5:"false";s:17:"enable_areyousure";s:5:"false";s:19:"auto_respond_enable";s:5:"false";s:17:"auto_respond_html";s:5:"false";s:13:"border_enable";s:5:"false";s:13:"aria_required";s:5:"false";s:12:"enable_reset";s:5:"false";s:18:"enable_credit_link";s:5:"false";s:23:"vcita_scheduling_button";s:5:"false";s:10:"vcita_link";s:5:"false";}', 'yes'),
