DELIMITER $$
--
-- Funktioner
--
CREATE DEFINER=`root`@`localhost` FUNCTION `margin`(buyPrice FLOAT, MSRP FLOAT) RETURNS float
BEGIN
  DECLARE margin FLOAT;
SET margin = MSRP-buyPrice;
RETURN margin;
END$$

CREATE DEFINER=`root`@`localhost` FUNCTION `marginPercetn`(buyPrice FLOAT, MSRP FLOAT) RETURNS varchar(20) CHARSET latin1
BEGIN
  DECLARE margin FLOAT;
SET margin = MSRP - buyPrice;
RETURN CONCAT((margin/MSRP)*100, "%");
END$$

DELIMITER ;

