INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2015-01-22 08:29:05', '2015-01-22 08:29:05', 'Välkommen till WordPress. Det här är ditt första inlägg. Redigera det eller radera det. Sedan kan du börja blogga!', 'Hej världen!', '', 'publish', 'open', 'open', '', 'hej-varlden', '', '', '2015-01-22 10:32:17', '2015-01-22 10:32:17', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2015-01-22 08:29:05', '2015-01-22 08:29:05', 'Detta är en exempelsida. Den skiljer sig från ett blogginlägg eftersom den är statisk och kommer att finnas på samma plats hela tiden, den kommer troligen att visas i din sidnavigering (i de flesta teman). De flesta börjar med en Om-sida som introducerar dem för potentiella besökare. Den skulle t.ex kunna ha följande innehåll:\r\n<blockquote>Hej där! Jag är cykelbud på dagen, blivande skådespelare på natten och detta är min blogg. Jag bor i Örebro, har en katt som heter Lurv och jag gillar mitt morgonkaffe (och att simma i Göta kanal).</blockquote>\r\n... eller något liknande detta:\r\n<blockquote>Den officiella svenska portalen för WordPress, WordPress Sverige grundades 2007 och tillhandahåller svenska översättningar för WordPress, teman och tillägg. Behöver man hjälp med att komma igång eller stöter på problem så är WordPress Sverige den perfekta platsen att få svensk support på. Du hittar portalen på wpsv.se.</blockquote>\r\nSom ny WordPress-användare bör du gå till <a href="http://localhost/wordpress/wp-admin/">din adminpanel</a> för att ta bort denna sida och sedan skapa nya sidor för ditt innehåll. Ha det så kul!', 'Exempelsida', '', 'publish', 'open', 'open', '', 'exempelsida', '', '', '2015-01-29 10:25:09', '2015-01-29 10:25:09', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(4, 1, '2015-01-22 08:48:52', '2015-01-22 08:48:52', 'Här kan man skriva massa saker.\r\n\r\nmvh Jens', 'OJojojojj', '', 'publish', 'open', 'open', '', 'ojojojojj', '', '', '2015-01-22 10:31:29', '2015-01-22 10:31:29', '', 0, 'http://localhost/wordpress/?p=4', 0, 'post', '', 0),
(5, 1, '2015-01-22 08:48:52', '2015-01-22 08:48:52', 'Här kan man skriva massa saker.\r\n\r\nmvh Jens', 'OJojojojj', '', 'inherit', 'open', 'open', '', '4-revision-v1', '', '', '2015-01-22 08:48:52', '2015-01-22 08:48:52', '', 4, 'http://localhost/wordpress/?p=5', 0, 'revision', '', 0),
